import Article from "@/store/modules/Article";

@Component({ components: { CommonLoader, CommentAdd, CommentDisplay } })
export default class ArticleComments extends Vue {
  @Prop({ required: true }) slug!: string;

  comments: IComment[] = [];
  isLoading = false;

  refreshComments(): void {
    const comments: IComment[] = [];
    Object.values(Article.commentsCache[this.slug] || [])?.forEach(comment => {
      if (Article.commentsCache[this.slug]?.[comment.id]) {
        comments.push(Article.commentsCache[this.slug][comment.id]);
      }
    });
    comments.sort((a, b) => (a.createdAt > b.createdAt ? 1 : -1));
    this.comments = comments;
  }

  @Watch("slug", { immediate: true })
  async onSlugChange(slug: string): Promise<void> {
    this.isLoading = true;
    try {
      if (slug) {
        await Article.fetchComments(slug);
        this.refreshComments();
      }
    } finally {
      this.isLoading = false;
    }
  }
}

import { Component, Prop, Vue } from "vue-property-decorator";

import CommonErrorsList from "@/components/CommonErrorsList.vue";
import { IArticle } from "@/services/realWorldApi/models";
import Article from "@/store/modules/Article";
import { isArrayOfStrings } from "@/utils/ArrayUtils";
import { notifySuccess } from "@/utils/NotificationUtils";

@Component({
  components: {
    CommonErrorsList
  }
})
export default class ArticleEditor extends Vue {
  @Prop() readonly article: IArticle | undefined;

  isLoading = false;
  title = this.article?.title || "";
  description = this.article?.description || "";
  body = this.article?.body || "";
  tagList = "";
  existingTagList: string[] = this.article?.tagList?.slice() || [];
  errors: string[] = [];

  removeTag(tag: string): void {
    const index = this.existingTagList.indexOf(tag);
    this.existingTagList.splice(index, 1);
  }

  async publish(): Promise<void> {
    this.errors = [];

    this.isLoading = true;
    try {
      let article;
      if (this.article) {
        let newTagsList = this.existingTagList.slice();
        if (this.tagList.length > 0) {
          newTagsList = newTagsList.concat(this.tagList.split(","));
        }
        article = await Article.update({
          slug: this.article.slug,
          params: {
            title: this.title,
            description: this.description,
            body: this.body,
            tagList: newTagsList
          }
        });
        notifySuccess("Article was successfully edited, redirecting...");
      } else {
        article = await Article.create({
          title: this.title,
          body: this.body,
          tagList: this.tagList.split(","),
          description: this.description
        });
        notifySuccess("Article was successfully created, redirecting...");
      }

      this.$router.push({
        name: this.$routesNames.articleView,
        params: { slug: article.slug }
      });
    } catch (e) {
      if (isArrayOfStrings(e)) {
        this.errors = e;
      } else {
        throw e;
      }
    } finally {
      this.isLoading = false;
    }
  }
}

