import IPagination, {
  DEFAULT_ITEMS_PER_PAGE,
  DEFAULT_START_PAGE
} from "@/services/common/IPagination";
import { IArticleList, IProfile } from "@/services/realWorldApi/models";
import Article from "@/store/modules/Article";
import Profile from "@/store/modules/Profile";
import User from "@/store/modules/User";

enum FeedType {
  Favorites = "favorites",
  My = "my"
}

Component.registerHooks(["beforeRouteEnter", "beforeRouteUpdate"]);

@Component({
  components: {
    CommonLoader,
    CommonFeed,
    ProfileFollowButton
  }
})
export default class ProfileIndex extends Vue {
  _profile: IProfile = { username: "", bio: "", image: "", following: false };
  isLoading = false;
  isFollowActionInProgress = false;
  currentPage = DEFAULT_START_PAGE;
  itemsPerPage = DEFAULT_ITEMS_PER_PAGE;

  activeTabId: FeedType = FeedType.My;
  activeFeed: IArticleList = { articles: [], articlesCount: 0 };
  activeTag: string | null = null;

  get profile(): IProfile {
    return Profile.profilesCache[this._profile?.username] || this._profile;
  }

  get tabs(): IFeedTab[] {
    const myTitle = this.isMyProfile
      ? "My Articles"
      : `${this.profile.username}'s Articles`;
    return [
      {
        id: FeedType.My,
        title: myTitle
      },
      {
        id: FeedType.Favorites,
        title: "Favorited Articles"
      }
    ];
  }

  get isMyProfile(): boolean {
    return this.profile.username === User.currentUser?.username;
  }

  beforeRouteEnter(to: Location, from: Location, next: Function): void {
    next((vm: ProfileIndex) => {
      vm.onRouteUpdate(to, from, next);
    });
  }
  beforeRouteUpdate(to: Location, from: Location, next: Function): void {
    this.onRouteUpdate(to, from, next);
  }

  async onRouteUpdate(
    to: Location,
    from: Location,
    next: Function
  ): Promise<void> {
    next();

    this.isLoading = true;
    try {
      const toUserName = to?.params?.username;
      const fromUserName = from?.params?.username;
      if (!toUserName) {
        this.$router.push({ name: this.$routesNames.home });
        return;
      }
      if (toUserName !== fromUserName) {
        this._profile = await Profile.get(toUserName);
      }

      const tabId = to?.params?.tabId;
      if (tabId && Object.values(FeedType).some(v => (v as string) === tabId)) {
        this.activeTabId = tabId as FeedType;
      } else {
        this.activeTabId = FeedType.My;
      }

      await this.fetchFeed();
    } catch (e) {
      this.$router.push({ name: this.$routesNames.home });
    } finally {
      this.isLoading = false;
    }
  }

  async onTabChanged(tabId: FeedType): Promise<void> {
    switch (tabId) {
      case FeedType.My:
      case FeedType.Favorites:
        this.activeTabId = tabId;
        this.currentPage = DEFAULT_START_PAGE;
        this.$router.push({
          params: { username: this.profile.username, tabId: this.activeTabId }
        });
        await this.fetchFeed();
        break;

      default:
        throw new Error(`Unexpected tabId: ${tabId}`);
    }
  }

  async onPageChanged(page: number): Promise<void> {
    this.currentPage = page;
    await this.fetchFeed();
  }

  async fetchFeed(): Promise<void> {
    this.isLoading = true;
    try {
      const pagination: IPagination = {
        limit: this.itemsPerPage,
        offset: (this.currentPage - 1) * this.itemsPerPage
      };

      switch (this.activeTabId) {
        case FeedType.Favorites:
          this.activeFeed = await Article.getList({
            favorited: this.profile.username,
            ...pagination
          });
          break;
        case FeedType.My:
          this.activeFeed = await Article.getList({
            author: this.profile.username,
            ...pagination
          });
          break;
        default:
          break;
      }
    } finally {
      this.isLoading = false;
      window.scrollTo(0, 0);
    }
  }
}

import { Component, Vue, Watch } from "vue-property-decorator";

import CommonErrorsList from "@/components/CommonErrorsList.vue";
import CommonLoader from "@/components/CommonLoader.vue";
import { ICurrentUser } from "@/store/models";
import User from "@/store/modules/User";
import { isArrayOfStrings } from "@/utils/ArrayUtils";

@Component({
  components: {
    CommonLoader,
    CommonErrorsList
  }
})
export default class ProfileSettings extends Vue {
  isLoading = false;

  image: string | null = null;
  bio = "";
  email = "";
  password: string | null = null;
  username = "";
  errors?: string[] = [];

  get hasErrors(): boolean {
    return !!this.errors?.length;
  }

  get currentUser(): Partial<ICurrentUser> {
    return User.currentUser || {};
  }

  logout(): void {
    User.logout();
    this.$router.push({ name: this.$routesNames.home });
  }

  async updateSettings(): Promise<void> {
    this.errors = [];

    this.isLoading = true;
    try {
      await User.update({
        email: this.email,
        password: this.password,
        bio: this.bio,
        image: this.image,
        username: this.username
      });
      this.$router.push({ name: this.$routesNames.home });
    } catch (e) {
      if (isArrayOfStrings(e)) {
        this.errors = e;
      } else {
        throw e;
      }
    } finally {
      this.isLoading = false;
    }
  }
  @Watch("currentUser", { immediate: true })
  onCurrentUserLoaded(newValue: ICurrentUser): void {
    this.errors = [];
    if (newValue) {
      this.image = newValue.image;
      this.bio = newValue.bio;
      this.email = newValue.email;
      this.username = newValue.username;
    }
  }
}
